import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';
import ExpenseList from '../components/ExpenseList';
import ExpenseForm from '../components/ExpenseForm';
import CategoryForm from '../components/CategoryForm';
import './Expenses.css';

function Expenses() {
  const [expenses, setExpenses] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showExpenseForm, setShowExpenseForm] = useState(false);
  const [showCategoryForm, setShowCategoryForm] = useState(false);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [expensesData, categoriesData] = await Promise.all([
        api.get('/expenses'),
        api.get('/categories')
      ]);
      setExpenses(expensesData.data);
      setCategories(categoriesData.data);
    } catch (error) {
      console.error('Erro ao carregar dados:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddExpense = async (expenseData) => {
    try {
      await api.post('/expenses', expenseData);
      await loadData();
      setShowExpenseForm(false);
    } catch (error) {
      console.error('Erro ao adicionar despesa:', error);
      alert('Erro ao adicionar despesa');
    }
  };

  const handleAddCategory = async (categoryData) => {
    try {
      await api.post('/categories', categoryData);
      await loadData();
      setShowCategoryForm(false);
    } catch (error) {
      console.error('Erro ao adicionar categoria:', error);
      alert('Erro ao adicionar categoria');
    }
  };

  const handleDeleteExpense = async (id) => {
    if (window.confirm('Tem certeza que deseja deletar esta despesa?')) {
      try {
        await api.delete(`/expenses/${id}`);
        await loadData();
      } catch (error) {
        console.error('Erro ao deletar despesa:', error);
        alert('Erro ao deletar despesa');
      }
    }
  };

  const totalExpenses = expenses.reduce((sum, expense) => sum + expense.amount, 0);

  return (
    <div className="expenses-page">
      <nav className="navbar">
        <div className="nav-content">
          <h2 className="nav-logo">ðŸ’° Controle de Custos</h2>
          <div className="nav-links">
            <Link to="/dashboard" className="nav-link">Dashboard</Link>
            <Link to="/expenses" className="nav-link active">Despesas</Link>
            <Link to="/profile" className="nav-link">Perfil</Link>
          </div>
        </div>
      </nav>

      <div className="expenses-container">
        <div className="expenses-header">
          <h1>Todas as Despesas</h1>
          <p className="subtitle">Gerencie todas as suas despesas</p>
        </div>

        <div className="stats-card">
          <div className="stat-item">
            <span className="stat-label">Total de Despesas</span>
            <span className="stat-value">R$ {totalExpenses.toFixed(2)}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Quantidade</span>
            <span className="stat-value">{expenses.length}</span>
          </div>
        </div>

        <div className="actions-bar">
          <button
            className="btn btn-primary"
            onClick={() => setShowExpenseForm(!showExpenseForm)}
          >
            {showExpenseForm ? 'Cancelar' : '+ Nova Despesa'}
          </button>
          <button
            className="btn btn-secondary"
            onClick={() => setShowCategoryForm(!showCategoryForm)}
          >
            {showCategoryForm ? 'Cancelar' : '+ Nova Categoria'}
          </button>
        </div>

        {showCategoryForm && (
          <CategoryForm
            onSubmit={handleAddCategory}
            onCancel={() => setShowCategoryForm(false)}
          />
        )}

        {showExpenseForm && (
          <ExpenseForm
            categories={categories}
            onSubmit={handleAddExpense}
            onCancel={() => setShowExpenseForm(false)}
          />
        )}

        {loading ? (
          <div className="loading">Carregando...</div>
        ) : (
          <ExpenseList
            expenses={expenses}
            onDelete={handleDeleteExpense}
          />
        )}
      </div>
    </div>
  );
}

export default Expenses;
