import React, { useState } from 'react';
import './ExpenseForm.css';

function ExpenseForm({ categories, onSubmit, onCancel }) {
  const [formData, setFormData] = useState({
    description: '',
    amount: '',
    date: new Date().toISOString().split('T')[0],
    category: categories.length > 0 ? categories[0]._id : ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.description || formData.description.trim() === '') {
      newErrors.description = 'A descrição é obrigatória';
    }
    
    if (!formData.amount || parseFloat(formData.amount) <= 0) {
      newErrors.amount = 'O valor deve ser maior que zero';
    }
    
    if (!formData.category) {
      newErrors.category = 'Selecione uma categoria';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    onSubmit({
      ...formData,
      amount: parseFloat(formData.amount)
    });
    setFormData({
      description: '',
      amount: '',
      date: new Date().toISOString().split('T')[0],
      category: categories.length > 0 ? categories[0]._id : ''
    });
    setErrors({});
  };

  return (
    <div className="form-container">
      <form className="expense-form" onSubmit={handleSubmit}>
        <h2>Nova Despesa</h2>
        
        <div className="form-group">
          <label htmlFor="description">Descrição</label>
          <input
            type="text"
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
            placeholder="Ex: Compra no supermercado"
            required
            aria-invalid={errors.description ? 'true' : 'false'}
            aria-describedby={errors.description ? 'description-error' : undefined}
          />
          {errors.description && (
            <span id="description-error" className="error-text" role="alert">
              {errors.description}
            </span>
          )}
        </div>

        <div className="form-group">
          <label htmlFor="amount">Valor (R$)</label>
          <input
            type="number"
            id="amount"
            name="amount"
            value={formData.amount}
            onChange={handleChange}
            placeholder="0.00"
            step="0.01"
            min="0"
            required
            aria-invalid={errors.amount ? 'true' : 'false'}
            aria-describedby={errors.amount ? 'amount-error' : undefined}
          />
          {errors.amount && (
            <span id="amount-error" className="error-text" role="alert">
              {errors.amount}
            </span>
          )}
        </div>

        <div className="form-group">
          <label htmlFor="date">Data</label>
          <input
            type="date"
            id="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            required
            aria-label="Data da despesa"
          />
        </div>

        <div className="form-group">
          <label htmlFor="category">Categoria</label>
          <select
            id="category"
            name="category"
            value={formData.category}
            onChange={handleChange}
            required
            aria-invalid={errors.category ? 'true' : 'false'}
            aria-describedby={errors.category ? 'category-error' : undefined}
          >
            {categories.length === 0 ? (
              <option value="">Nenhuma categoria disponível</option>
            ) : (
              categories.map(cat => (
                <option key={cat._id} value={cat._id}>
                  {cat.name}
                </option>
              ))
            )}
          </select>
          {errors.category && (
            <span id="category-error" className="error-text" role="alert">
              {errors.category}
            </span>
          )}
        </div>

        <div className="form-actions">
          <button type="submit" className="btn btn-primary">
            Adicionar Despesa
          </button>
          <button type="button" className="btn btn-secondary" onClick={onCancel}>
            Cancelar
          </button>
        </div>
      </form>
    </div>
  );
}

export default ExpenseForm;
